sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if ( newValue == '') {
      return;
   }

	var whatneeded = g_form.getValue('u_what_needed');

   // Limpe todas as opções da lista de opções de campo What needed
	g_form.clearOptions('u_what_needed');

	// Se o valor do campo Tipo de solicitação for Recursos humanos, adicione
	// duas opções de recursos humanos e outra para a lista de opções de campo What needed
	if(newValue == 'hr'){
		g_form.addOption('u_what_needed', 'hr1', 'Recursos humanos 1');
		g_form.addOption('u_what_needed', 'hr2', 'Recursos humanos 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}
	// Se o valor do campo Tipo de solicitação for Facilities, adicione
	// duas opções de Facilities e outra para a lista de opções do campo What needed
	if(newValue == 'facilities'){
		g_form.addOption('u_what_needed', 'facilities1', 'Facilities 1');
		g_form.addOption('u_what_needed', 'facilities2', 'Facilities 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}
	// Se o valor do campo Tipo de solicitação for Legal, adicione
	// duas opções de Facilities e outra para a lista de opções do campo What needed
	if(newValue == 'legal'){
		g_form.addOption('u_what_needed', 'legal1', 'Legal 1');
		g_form.addOption('u_what_needed', 'legal2', 'Legal 2');
		g_form.addOption('u_what_needed', 'other', 'Other');
	}

	// Se o formulário estiver carregando e não for um novo registro, defina o valor u_what_needed para o valor do registro antes de ser carregado
	if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
	}
}
